const input = $node["Scrape Profiles + Posts - Relevance AI"].json;

// Try to get posts from output, then fallback to top-level.
const posts = (input.output && input.output.last_30_days_posts_transformed) 
  || input.last_30_days_posts_transformed 
  || [];

// Build HTML for each LinkedIn post
let postsHTML = '';
if (Array.isArray(posts)) {
  posts.forEach(post => {
    postsHTML += `<div class="linkedin-post" style="border:1px solid #ddd; padding:10px; margin-bottom:10px;">
      <p>${post.user_post}</p>
      <p><em>Posted on: ${post.posted}</em></p>
    </div>`;
  });
}

// Return only the generated HTML
return [{ json: { postsHTML } }];

