// Extract the citations array; default to an empty array if not present
const citations = input.citations || [];

// Build an HTML unordered list for the citations
let citationsHTML = '<ul>';
if (Array.isArray(citations)) {
  citations.forEach(citation => {
    citationsHTML += `<li><a href="${citation}" target="_blank">${citation}</a></li>`;
  });
}
citationsHTML += '</ul>';

// Return a new JSON object containing the generated HTML snippet
return [{ json: { citationsHTML } }];
